__author__ = 'mzkumaresan'import matplotlib.pyplot as plt# original count# result = {'positive': 47861, 'neutral': 121529, 'negative': 17253, 'verynegative': 6729, 'verypositive': 41419}# cleaned EH# result = {'positive': 48069, 'neutral': 122626, 'negative': 18051, 'verynegative': 6652, 'verypositive': 40369}# cleaned Chapter tweetsresult = {'positive': 2872, 'neutral': 8221, 'negative': 450, 'verynegative': 53, 'verypositive': 2077}# The slices will be ordered and plotted counter-clockwise.labels = list()sizes = list()totalsize = 0for key in result.keys():    labels.append(key)    sizes.append(result.__getitem__(key))colors = ['yellowgreen', 'gold', 'lightskyblue', 'lightcoral', 'red']explode = (0.1, 0, 0, 0, 0) # only "explode" the 2nd slice (i.e. 'Hogs')plt.pie(sizes, explode=explode, labels=labels, colors=colors,        autopct='%1.1f%%', shadow=True, startangle=90)# Set aspect ratio to be equal so that pie is drawn as a circle.plt.axis('equal')plt.show()