__author__ = 'mzkumaresan'import csvimport vaderSentimentfrom vaderSentiment.vaderSentiment import sentiment as vatweets = list()words  = list()neu = 0pos = 0neg = 0veryneg = 0verypos = 0# path = './DKSG-EarthHour/'path = 'hashtag_tweets - nocomma - checked_remap.csv'result = dict()# with open(path + 'tweets/test1.csv', 'r') as csvfile:with open(path, 'r') as csvfile:    tweetreader = csv.reader(csvfile)    for row in tweetreader:        tweet = row[2]        vs = va(tweet)        score = vs.__getitem__("compound")        if score <= -0.56:            score1 = 'very neg'            veryneg = veryneg + 1        else:            if score > -0.56 and score <= -0.11:                score1 = 'neg'                neg = neg + 1            else:                if score > -0.11 and score <= 0.09:                    score1 = 'neu'                    neu = neu + 1                else:                    if score > 0.09 and score <= 0.54:                         score1 = 'pos'                         pos  = pos + 1                    else:                         score1 = 'very pos'                         verypos =  verypos + 1        row.append(str(score))        row.append(str(score1))        tweets.append(row)result.__setitem__('neutral',neu)result.__setitem__('positive',pos)result.__setitem__('verypositive',verypos)result.__setitem__('verynegative',veryneg)result.__setitem__('negative',neg)print (result)